let count=0;let timer=10000;function sliderTimer(){const images=document.querySelectorAll('.carusel__item');if(count===0){images[count].classList.toggle('carusel__item--active');images[images.length-1].classList.remove('carusel__item--active')}else{images[count].classList.toggle('carusel__item--active');images[count-1].classList.toggle('carusel__item--active')}count++;if(images[count]===undefined){count=0}setTimeout(sliderTimer,timer)}sliderTimer();